void TI_CCTALK_Init(void)
{
  /* FTM0_SC: TOF=0,TOIE=0,CPWMS=0,CLKS=0,PS=0 */
  setReg8(FTM0_SC, 0x00U);             /* Stop HW; disable overflow interrupt and set prescaler to 0 */ 
  /* FTM0_MOD: MOD=0 */
  setReg16(FTM0_MOD, 0x00U);           /* Clear modulo register: e.g. set free-running mode */ 
  /* FTM0_C0SC: CHF=0,CHIE=1,MSB=0,MSA=1,ELSB=0,ELSA=0,??=0,??=0 */
  setReg8(FTM0_C0SC, 0x50U);           /* Set output compare mode and enable compare interrupt */ 
  TI_CCTALK_SetCV(0xC350U);            /* Initialize appropriate value to the compare/modulo/reload register */
  /* FTM0_CNTH: COUNT_H=0 */
  setReg8(FTM0_CNTH, 0x00U);           /* Reset HW Counter */ 
  /* FTM0_SC: TOF=0,TOIE=0,CPWMS=0,CLKS=1,PS=4 */
  setReg8(FTM0_SC, 0x0CU);             /* Set prescaler */ 
}


